// display visual ui notifications (currently with pnotify)
function notify(message, severity, title) {
    var title = typeof title !== 'undefined' ? title : null;
    var severity = typeof severity !== 'undefined' ? severity : 'notice';

    var opts = {
        text: message,
        hide: false,
        shadow: false,
        buttons: { closer_hover: true, sticker: false},
        confirm: { confirm: false }
    };

    switch (severity) {
    case 'alert':
    case 'error':
        opts.title = "Error";
        opts.type = "error";
        break;
    case 'warn':
    case 'warning':
    case 'notice':
        opts.title = "Notice";
        opts.type = "notice";
        break;
    case 'info':
        opts.title = "Info";
        opts.type = "info";
        break;
    case 'success':
        opts.title = "Success";
        opts.type = "success";
        break;
    }

    new PNotify(opts);

}

